        for($i=0; $i&lt;$count; $i++)&#123;
            $card = $this->cardsPool->draw();

            if($card["stars"] == 6)&#123;
                $this->session->set('', $card["No"]);
            &#125;

            $result[] = $card;
        &#125;

        $this->session->save();

        return $result;
    &#125;

